import React from 'react';
import '../../Body.css';
import './AddPublication.css';
import Header from '../../../Components/Header/Header';
import Footer from '../../../Components/Footer/Footer';

export default function AddPublication (){

    return(

        <div>

            <Header />

            <div className='body add-publication'>

                <div className='add-publication-content'>
                <p>Add new author</p>

                <input className='add-publication-inputs' type="text" placeholder='Title' />
                <input className='add-author-inputs' type="text" placeholder='Description' />
                <input className='add-author-inputs' type="text" placeholder='Edition' />
                <input className='add-author-inputs' type="text" placeholder='Date Published' />
                <input className='add-author-inputs' type="text" placeholder='Quantity' />
                <input className='add-author-inputs' type="text" placeholder='Number of Pages' />

                    
                <div className='add-author-buttons-container edit-buttons'>
                    <button className='add-author-buttons'>Edit</button>
                    <button className='add-author-buttons'>Save Changes</button>
                    <button className='add-author-buttons'>Add</button>
                </div>

            </div>

            </div>

            <Footer />

        </div>

    )

}
