import React, { useState, useEffect } from 'react';
import './UserHome.css';

export default function UserHome (){

    
    const options = [

        { label: 'Newest Publications', value: 'newest publications' },
     
        { label: 'Oldest Publications', value: 'oldest publications' },
     
        { label: 'Genre', value: 'genre' },
     
        ];
     

      const [value, setValue] = useState('newest publications');
     
      const handleChange = (event) => {
     
        setValue(event.target.value);
     
      };


      const Dropdown = ({ label, value, options, onChange }) => {

        return (
       
          <label>
       
            {label}
       
            <select value={value} onChange={onChange}>
       
              {options.map((option) => (
       
                <option value={option.value}>{option.label}</option>
       
              ))}
       
            </select>
       
          </label>
       
        );
       
       };



    return(

        <div className='user-home'>

            <input placeholder="Filter Publications" />
            <button>SEARCH</button>

            <Dropdown
                label="What do we eat?"
                options={options}
                value={value}
                onChange={handleChange}
            />

            <p>We eat {value}!</p>


            <div className='publications'>
                <p className='publication'>test1</p>
                <p className='publication'>test2</p>
                <p className='publication'>test3</p>
            </div>

        </div>

    )

}
